mapper.select:
        select
        `id`,`num`,`batch_num`,`type_name`,`variety_name`,`inputs_id`,`direction`,`occurrence_time`,`dept_id`,`other_info`,`create_user`,`create_time`,`update_user`,`update_time`,`remark1`,`remark2`,`remark3`
        from bs_access_record



            and id = #{id}
            and num = #{num}
            and batch_num = #{batchNum}
            and type_name = #{typeName}
            and variety_name = #{varietyName}
            and inputs_id = #{inputsId}
            and direction = #{direction}
            and occurrence_time = #{occurrenceTime}
            and dept_id = #{deptId}
            and other_info = #{otherInfo}
            and create_user = #{createUser}
            and create_time = #{createTime}
            and update_user = #{updateUser}
            and update_time = #{updateTime}
            and remark1 = #{remark1}
            and remark2 = #{remark2}
            and remark3 = #{remark3}


							and bs_access_record.occurrence_time >= #{startOccurrenceTime}




							and bs_access_record.occurrence_time <= #{endOccurrenceTime}





                order by ${sort} ${order}


                order by id desc



            limit #{offset}, #{limit}

        select count(*)
		from bs_access_record



			and id = #{id}
			and num = #{num}
			and batch_num = #{batchNum}
			and type_name = #{typeName}
			and variety_name = #{varietyName}
			and inputs_id = #{inputsId}
			and direction = #{direction}
			and occurrence_time = #{occurrenceTime}
			and dept_id = #{deptId}
			and other_info = #{otherInfo}
			and create_user = #{createUser}
			and create_time = #{createTime}
			and update_user = #{updateUser}
			and update_time = #{updateTime}
			and remark1 = #{remark1}
			and remark2 = #{remark2}
			and remark3 = #{remark3}


							and bs_access_record.occurrence_time >= #{startOccurrenceTime}




							and bs_access_record.occurrence_time <= #{endOccurrenceTime}
