mapper.select:
		SELECT
			bs_sale_record.`id`,
			bs_sale_record.`num`,
			bs_sale_record.`batch_num`,
			bs_sale_record.`type_name`,
			bs_sale_record.`variety_name`,
			bs_sale_record.`inputs_id`,
			bs_sale_record.`customer_id`,
			bs_sale_record.`dept_id`,
			bs_sale_record.`other_info`,
			bs_sale_record.`create_user`,
			bs_sale_record.`create_time`,
			bs_sale_record.`update_user`,
			bs_sale_record.`update_time`,
			bs_sale_record.`sale_num`,
			bs_sale_record.`remark1`,
			bs_sale_record.`remark2`,
			bs_sale_record.`remark3`,
			bs_customer.`name` customer_name
		FROM
			bs_sale_record
		LEFT JOIN bs_customer ON bs_sale_record.customer_id = bs_customer.id



		  		   and id = #{id} 
		  		   and num = #{num} 
		  		   and batch_num = #{batchNum} 
		  		   and type_name = #{typeName} 
		  		   and variety_name = #{varietyName} 
		  		   and inputs_id = #{inputsId} 
		  		   and customer_id = #{customerId} 
		  		   and dept_id = #{deptId} 
		  		   and other_info = #{otherInfo} 
		  		   and create_user = #{createUser} 
		  		   and create_time = #{createTime} 
		  		   and update_user = #{updateUser} 
		  		   and update_time = #{updateTime} 
		  		    and sale_num = #{saleNum} 
		  		   and remark1 = #{remark1} 
		  		   and remark2 = #{remark2} 
		  		   and remark3 = #{remark3} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}
