mapper.select:
		select
		bs_inputs.`id`,
		bs_inputs.`num`,
		bs_inputs.`batch_num`,
		bs_inputs.`type_name`,
		bs_inputs.`variety_name`,
		bs_inputs.`init_weight`,
		bs_inputs.`cur_weight`,
		bs_inputs.`dept_id`,
		bs_inputs.`space_id`,
		bs_inputs.`other_info`,
		bs_inputs.`status`,
		bs_inputs.`parent_id`,
		pp.parent_id pparent_id,
		bs_inputs.`birthday`,
		bs_inputs.`create_user`,
		bs_inputs.`create_time`,
		bs_inputs.`update_user`,
		bs_inputs.`update_time`,
		bs_inputs.`remark1`,
		bs_inputs.`remark2`,
		bs_inputs.`remark3`,
		bs_space.name space_name,
		bs_supplier.`name` supplier_name
		from bs_inputs
		left join bs_space on bs_inputs.space_id = bs_space.id
		left join bs_supplier on bs_inputs.supplier_id = bs_supplier.id
		left join bs_inputs pp on bs_inputs.parent_id = pp.id

			left join bs_inputs ppp on pp.parent_id = ppp.id




		  		   and bs_inputs.id = #{id} 
		  		   and bs_inputs.num = #{num} 
		  		   and bs_inputs.batch_num = #{batchNum} 
		  		   and bs_inputs.type_name = #{typeName} 
		  		   and bs_inputs.variety_name = #{varietyName} 
		  		   and bs_inputs.init_weight = #{initWeight} 
		  		   and bs_inputs.cur_weight = #{curWeight} 
		  		   and bs_inputs.dept_id = #{deptId} 
		  		   and bs_inputs.space_id = #{spaceId} 
		  		   and bs_inputs.supplier_id = #{supplierId} 
		  		   and bs_inputs.other_info = #{otherInfo} 
		  		   and bs_inputs.status = #{status} 
		  		   and bs_inputs.create_user = #{createUser} 
		  		   and bs_inputs.create_time = #{createTime} 
		  		   and bs_inputs.update_user = #{updateUser} 
		  		   and bs_inputs.update_time = #{updateTime} 
		  		   and bs_inputs.remark1 = #{remark1} 
		  		   and bs_inputs.remark2 = #{remark2} 
				  and bs_inputs.remark3 = #{remark3} 

				  and pp.num = #{parentNum} 
				  and ppp.num = #{pparentNum} 


							and bs_inputs.birthday >= #{startBirthday}




							and bs_inputs.birthday <= #{endBirthday}






                order by ${sort} ${order}


                order by bs_inputs.id desc



			limit #{offset}, #{limit}

select count(*)
from bs_inputs
left join bs_space on bs_inputs.space_id = bs_space.id
left join bs_supplier on bs_inputs.supplier_id = bs_supplier.id
left join bs_inputs pp on bs_inputs.parent_id = pp.id

	left join bs_inputs ppp on pp.parent_id = ppp.id




	 and bs_inputs.id = #{id} 
	 and bs_inputs.num = #{num} 
	 and bs_inputs.batch_num = #{batchNum} 
	 and bs_inputs.type_name = #{typeName} 
	 and bs_inputs.variety_name = #{varietyName} 
	 and bs_inputs.init_weight = #{initWeight} 
	 and bs_inputs.cur_weight = #{curWeight} 
	 and bs_inputs.dept_id = #{deptId} 
	 and bs_inputs.space_id = #{spaceId} 
	 and bs_inputs.supplier_id = #{supplierId} 
	 and bs_inputs.other_info = #{otherInfo} 
	 and bs_inputs.status = #{status} 
	 and bs_inputs.create_user = #{createUser} 
	 and bs_inputs.create_time = #{createTime} 
	 and bs_inputs.update_user = #{updateUser} 
	 and bs_inputs.update_time = #{updateTime} 
	 and bs_inputs.remark1 = #{remark1} 
	 and bs_inputs.remark2 = #{remark2} 
	 and bs_inputs.remark3 = #{remark3} 

	 and pp.num = #{parentNum} 
	 and ppp.num = #{pparentNum} 


					and bs_inputs.birthday >= #{startBirthday}




					and bs_inputs.birthday <= #{endBirthday}
