mapper.select:
		select
				bs_inputs.`id`,
				bs_inputs.`num`,
				bs_inputs.`batch_num`,
				bs_inputs.`type_name`,
				bs_inputs.`variety_name`,
				bs_inputs.`init_weight`,
				bs_inputs.`cur_weight`,
				bs_inputs.`dept_id`,
				bs_inputs.`space_id`,
				bs_inputs.`other_info`,
				bs_inputs.`status`,
				bs_inputs.`create_user`,
				bs_inputs.`create_time`,
				bs_inputs.`update_user`,
				bs_inputs.`update_time`,
				bs_inputs.`remark1`,
				bs_inputs.`remark2`,
				bs_inputs.`remark3`,
			    bs_space.name space_name,
			    bs_supplier.`name` supplier_name
       from bs_inputs
  left join bs_space on bs_inputs.space_id = bs_space.id
  left join bs_supplier on bs_inputs.supplier_id = bs_supplier.id



		  		   and id = #{id} 
		  		   and num = #{num} 
		  		   and batch_num = #{batchNum} 
		  		   and type_name = #{typeName} 
		  		   and variety_name = #{varietyName} 
		  		   and init_weight = #{initWeight} 
		  		   and cur_weight = #{curWeight} 
		  		   and dept_id = #{deptId} 
		  		   and space_id = #{spaceId} 
		  		   and supplier_id = #{supplierId} 
		  		   and other_info = #{otherInfo} 
		  		   and status = #{status} 
		  		   and create_user = #{createUser} 
		  		   and create_time = #{createTime} 
		  		   and update_user = #{updateUser} 
		  		   and update_time = #{updateTime} 
		  		   and  bs_inputs.remark1 = #{remark1} 
		  		   and  bs_inputs.remark2 = #{remark2} 
		  		   and  bs_inputs.remark3 = #{remark3} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}
